import React from 'react';

class Type extends React.Component {

  render() {

    const { value, readForm } = this.props;

    return(
        <div className="control">
        <p>Tipo:</p>
        <label className="radio">
          <input type="radio" name="apartment" value={value} onChange={readForm} required />
          Apartamento
        </label>
        <label className="radio">
          <input type="radio" name="house" value={value} onChange={readForm} />
          Casa
        </label>
      </div>
    )
  }
}

export default Type;
