import React from 'react';

class End extends React.Component {

  validEnd({ target }) {
    this.setState({
      end: target.value.replace(/[`~!@#$%^&*+=><']/g, "")
    })
  }

  render() {

    const { value, readForm } = this.props;

    return(
        <div className="end">

        <label className="label">Endereço:</label>
        <input name="end" className="input is-primary" type="text" validEnd placeholder="Digite seu Endereço" value={value} onChange={readForm} maxLength="200" required></input>


        </div>
    )
  }
}

export default End;
