import React from 'react';

class End extends React.Component {

  constructor() {
    super()


    if (validValue(value)) {
    this.state = {
      'end': '',
    }
  }
  }

  render() {

    let { value, readForm } = this.props;

    const validValue = (value) => {
      const query = ['ç ', '/`', '^', '~'];
      if (value.forEach(element => {
        element.includes(query[0])       
      })) {
        return true;
      }
    }

    return(
        <div className="end">

        <label className="label">Endereço:</label>
        <input name="end" className="input is-primary" type="text" placeholder="Digite seu Endereço" value={validValue(value)} onChange={readForm} maxLength="200" required></input>


        </div>
    )
  }
}

export default End;
